def solve():
    n = int(input())
    costs = list(map(int, input().split()))
    m = int(input())

    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u, v))

    adj = [[] for _ in range(n)]
    for u, v in edges:
        adj[u-1].append(v-1)

    def dfs(node, visited, path):
        visited[node] = True
        path.append(node)

        for neighbor in adj[node]:
            if not visited[neighbor]:
                dfs(neighbor, visited, path)

    def find_all_paths(start_node):
        visited = [False] * n
        path = []
        dfs(start_node, visited, path)

        all_paths = []
        for i in range(1 << n):
            temp_path = []
            for j in range(n):
                if (i >> j) & 1:
                    temp_path.append(path[j])
            all_paths.append(temp_path)

        return all_paths

    def check_security(path):
        for i in range(len(path)):
            for j in range(i + 1, len(path)):
                if path[i] == path[j]:
                    continue
                found = False
                for u, v in edges:
                    if path[i] == u and path[j] == v:
                        found = True
                        break
                    if path[i] == v and path[j] == u:
                        found = True
                        break
                if not found:
                    return False
        return True

    min_cost = float('inf')
    min_ways = 0

    for i in range(1 << n):
        paths = find_all_paths(0)
        valid_paths = 0
        for path in paths:
            if check_security(path):
                valid_paths += 1

        if valid_paths > 0:
            cost = sum(costs[j] for j in path)
            if cost < min_cost:
                min_cost = cost
                min_ways = valid_paths
            elif cost == min_cost:
                min_ways = (min_ways + valid_paths) % 1000000007

    print(min_cost, min_ways)


