                x_row = i
                x_col = j
                break
        if x_row != -1:
            break

    ways = []
    for i in range(k):
        ways.append(input())

    def is_valid(x, y):
        if x < 0 or x >= n or y < 0 or y >= m:
            return False
        if maze[x][y] == '*':
            return False
        return True

    def find_cycle(way, start_row, start_col, visited):
        if len(way) == k:
            ways.append(way)
            return

        for i in range(4):
            new_row = start_row + (i - 2) // 2
            new_col = start_col + (i - 2) % 2

            if is_valid(new_row, new_col) and (new_row, new_col) not in visited:
                visited.add((new_row, new_col))
                find_cycle(way + ways[i], new_row, new_col, visited)
                visited.remove((new_row, new_col))

    visited = set()
    visited.add((x_row, x_col))
    find_cycle('', x_row, x_col, visited)

    if not ways:
        print('IMPOSSIBLE')
        return

    ways.sort()
    print(ways[0])


