    n = int(input())
    segments = []
    for _ in range(n):
        l, r = map(int, input().split())
        segments.append((l, r))

    edges = []
    for i in range(n):
        for j in range(i + 1, n):
            l1, r1 = segments[i]
            l2, r2 = segments[j]

            if l1 < l2 < r1 < r2 or l1 < r2 < r1 < l2 or l2 < l1 < r2 < r1 or l2 < r1 < r2 < l1:
                edges.append((i, j))

    if len(edges) != n - 1:
        print('NO')
        return

    adj = [[] for _ in range(n)]
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    visited = [False] * n
    stack = [0]
    visited[0] = True

    while stack:
        u = stack.pop()
        for v in adj[u]:
            if not visited[v]:
                visited[v] = True
                stack.append(v)

    if len(visited) != n:
        print('NO')
        return

    print('YES')


