def solve():
    n, m, s, t = map(int, input().split())
    edges = []
    for _ in range(m):
        a, b, l = map(int, input().split())
        edges.append((a, b, l))

    def check_repair(road_index, cost):
        a, b, l = edges[road_index]
        if a == s or b == t:
            return False

        # Check if we can repair the road
        for i in range(m):
            if i != road_index:
                if edges[i][0] == a or edges[i][1] == a:
                    if edges[i][0] == b or edges[i][1] == b:
                        if edges[i][2] <= cost:
                            return False
        return True

    def find_min_cost(road_index, cost):
        a, b, l = edges[road_index]
        if a == s or b == t:
            return cost

        min_cost = float('inf')
        for i in range(m):
            if i != road_index:
                if edges[i][0] == a or edges[i][1] == a:
                    if edges[i][0] == b or edges[i][1] == b:
                        new_cost = find_min_cost(i, cost + abs(l - edges[i][2]))
                        if new_cost != float('inf'):
                            min_cost = min(min_cost, new_cost)
        return min_cost

    for i in range(m):
        if edges[i][0] == s or edges[i][1] == s:
            if edges[i][0] == t or edges[i][1] == t:
                print('YES')
            else:
                if check_repair(i, 0):
                    print('CAN', find_min_cost(i, 0))
                else:
                    print('NO')
        else:
            if check_repair(i, 0):
                print('CAN', find_min_cost(i, 0))
            else:
                print('NO')


