def solve():
    e = input().strip()
    p, m = map(int, input().split())

    def calculate(expression):
        stack = []
        num = ''
        for char in expression:
            if char.isdigit():
                num += char
            else:
                if num:
                    stack.append(int(num))
                    num = ''
                if char == '(':
                    stack.append(char)
                elif char == ')':
                    sub_expression = ''
                    while stack[-1] != '(':
                        sub_expression = stack.pop() + sub_expression
                    stack.pop()
                    stack.append(calculate(sub_expression))
                else:
                    if stack and stack[-1] == '+':
                        stack.pop()
                        stack.append(stack.pop() + stack.pop())
                    elif stack and stack[-1] == '-':
                        stack.pop()
                        second = stack.pop()
                        first = stack.pop()
                        stack.append(first - second)
        if num:
            stack.append(int(num))
        if len(stack) == 1:
            return stack[0]
        else:
            return None

    def find_max_value(p, m):
        max_val = 0
        for i in range(2**p):
            ops = []
            for j in range(p):
                if (i >> j) & 1:
                    ops.append('+')
                else:
                    ops.append('-')

            expression = e.replace('?', ' ').replace(' ', '')
            for op in ops:
                expression = expression.replace('?', op)

            val = calculate(expression)
            if val is not None:
                max_val = max(max_val, val)
        return max_val

    print(find_max_value(p, m))


solve()

