def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def find_path(start, end):
        visited = [False] * (n + 1)
        path = []
        q = [(start, [start])]
        while q:
            node, curr_path = q.pop(0)
            if node == end:
                path = curr_path
                break
            visited[node] = True
            for neighbor in range(1, n + 1):
                if not visited[neighbor] and (node, neighbor) in edges:
                    q.append((neighbor, curr_path + [neighbor]))
        return path

    max_common = 0
    max_path1 = []
    max_path2 = []

    for i in range(n):
        for j in range(i + 1, n):
            path1 = find_path(1, i)
            path2 = find_path(1, j)

            common = 0
            for node in path1:
                if node in path2:
                    common += 1

            if common > max_common:
                max_common = common
                max_path1 = path1
                max_path2 = path2

    print(*max_path1, *max_path2)


