def solve():
    n = int(input())
    board = []
    for _ in range(n):
        board.append(input())

    def is_valid_cross(x, y):
        if board[x][y] == '.':
            return False

        for dx in [-1, 0, 1]:
            for dy in [-1, 0, 1]:
                if dx == 0 and dy == 0:
                    continue
                nx, ny = x + dx, y + dy
                if 0 <= nx < n and 0 <= ny < n:
                    if board[nx][ny] == '#':
                        return False
        return True

    def count_crosses():
        crosses = 0
        for i in range(n):
            for j in range(n):
                if is_valid_cross(i, j):
                    crosses += 1
        return crosses

    crosses = count_crosses()
    if crosses == 0:
        print('YES')
        return

    total_cells = n * n
    if total_cells % 5 != 0:
        print('NO')
        return

    required_crosses = total_cells // 5

    if crosses >= required_crosses:
        print('YES')
    else:
        print('NO')


solve()

