        for i in range(n - 1):
            temp_s = list(s)
            temp_s[i], temp_s[i+1] = temp_s[i+1], temp_s[i]
            temp_s = ''.join(temp_s)
            val_temp = val(temp_s)
            if val_temp < min_val:
                min_val = val_temp
                min_str = temp_s

        # Replace "11" with "1"
