                temp_s[i] = '1'
                temp_s[i+1] = '0'
                temp_s = ''.join(temp_s)
                val_temp = val(temp_s)
                if val_temp < min_val:
                    min_val = val_temp
                    min_str = temp_s

        return get_min(min_str) if min_str else s

