    for _ in range(m):
        grid.append(list(map(int, input().split())))

    def is_valid(row, col, value):
        if row == 0 and col == 0:
            return True
        if row == 0:
            return grid[row][col-1] == value
        if col == 0:
            return grid[row-1][col] == value
        return grid[row-1][col] * grid[row][col-1] == value

    def count_ways(row, col, current_sum):
        if col == m:
            col = 0
            row += 1
        if row == m:
            return 1

        if grid[row][col] != 0:
            if row == 0 and col == 0:
                return 1
            if row == 0:
                return count_ways(row, col + 1, current_sum + grid[row][col])
            if col == 0:
                return count_ways(row + 1, col, current_sum + grid[row][col])
            if op == '+':
                return count_ways(row, col + 1, current_sum + grid[row][col])
            if op == '-':
                return count_ways(row, col + 1, current_sum - grid[row][col])
            if op == '*':
                return count_ways(row, col + 1, current_sum * grid[row][col])
            if op == '/':
                if grid[row][col] == 0:
                    return 0
                return count_ways(row, col + 1, current_sum // grid[row][col])
        else:
            count = 0
            for i in range(1, n + 1):
                if is_valid(row, col, i):
                    grid[row][col] = i
                    count += count_ways(row, col + 1, current_sum)
                    grid[row][col] = 0
            return count

    print(count_ways(0, 0, 0))


