import math


def solve():
    a, m = map(float, input().split())
    W, H, X, Y = map(int, input().split())
    grid = []
    for _ in range(H):
        grid.append(list(map(int, input().split())))

    def calculate_tide_level(t):
        return 0.5 * a * (math.cos(t * (2 * math.pi / 12)) + 1)

    def is_safe(x, y, t):
        current_height = grid[y][x]
        tide_level = calculate_tide_level(t)
        return abs(current_height - tide_level) <= 1

    def find_safe_path(start_x, start_y, end_x, end_y, t):
        queue = [(start_x, start_y, 0)]
        visited = set()
        visited.add((start_x, start_y))

        while queue:
            x, y, distance = queue.pop(0)

            if x == end_x and y == end_y:
                return distance

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = x + dx, y + dy

                if 0 <= nx < W and 0 <= ny < H and (nx, ny) not in visited:
                    current_height = grid[ny][nx]
                    tide_level = calculate_tide_level(t)

                    if abs(current_height - tide_level) <= 1:
                        visited.add((nx, ny))
                        queue.append((nx, ny, distance + 1))

        return -1

    max_distance = 0
    for t in range(12):
        max_distance = max(max_distance, find_safe_path(X, Y, X, Y, t))

    print(max_distance)


