def solve():
    n, m = map(int, input().split())
    cities = []
    for _ in range(n):
        cities.append(input())

    routes = []
    for _ in range(m):
        u, v, c = input().split()
        c = int(c)
        routes.append((u, v, c))

    assignments = []
    for _ in range(4):
        u, v = input().split()
        assignments.append((u, v))

    def find_path(start, end, visited, graph):
        if start == end:
            return True
        visited.add(start)
        for neighbor in graph[start]:
            if neighbor not in visited:
                if find_path(neighbor, end, visited, graph):
                    return True
        return False

    def calculate_cost(assignment):
        u, v = assignment
        cost = 0
        graph = {}
        for u1, v1, c in routes:
            if u1 not in graph:
                graph[u1] = []
            graph[u1].append(v1)
            if v1 not in graph:
                graph[v1] = []
            graph[v1].append(u1)

        visited = set()
        if find_path(u, v, visited, graph):
            return 0
        else:
            for u1, v1, c in routes:
                if u1 == u or u1 == v or v1 == u or v1 == v:
                    continue
                if find_path(u, v, set(), {u, v}):
                    cost += c
        return cost

    min_cost = float('inf')
    for i in range(1 << 4):
        current_assignment = []
        for j in range(4):
            if (i >> j) & 1:
                current_assignment.append(assignments[j])

        current_cost = 0
        for assignment in current_assignment:
            current_cost += calculate_cost(assignment)

        min_cost = min(min_cost, current_cost)

    print(min_cost)


