def solve():
    s = input()
    n = len(s)
    lights = [False] * n

    def toggle(lights, index):
        for i in range(index, n, index + 1):
            lights[i] = not lights[i]

    def check_all_on(lights):
        for light in lights:
            if not light:
                return False
        return True

    def find_earliest_time(lights, s):
        for i in range(n):
            if s[i] == '1':
                toggle(lights, i)

        time = 0
        while not check_all_on(lights):
            time += 1
            for i in range(n):
                if s[i] == '1':
                    toggle(lights, i)

        return time

    print(find_earliest_time(lights, s))


