def solve():
    n, x, y = map(int, input().split())

    mod = 10**9 + 7

    def combinations(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = (res * (n - i)) % mod
            res = (res * pow(i + 1, mod - 2, mod)) % mod
        return res

    def calculate_paths(n, x, y):
        if x > n or y > n:
            return 0

        paths = 0

        for i in range(x, n + 1):
            for j in range(y, n + 1):
                paths = (paths + combinations(n, i) *
                         combinations(n, j) % mod) % mod

        return paths

    print(calculate_paths(n, x, y))


