def solve():
    n, m, alpha = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v, c = map(int, input().split())
        edges.append((u, v, c))

    def calculate_energy(path):
        l = max(c for u, v, c in path)
        k = len(path)
        return l**2 + alpha * k

    def find_path(start, end, visited, path):
        if start == end:
            return path

        visited[start] = True
        for u, v, c in edges:
            if not visited[u] and u == start:
                new_path = path + [(u, v, c)]
                result = find_path(v, end, visited[:], new_path)
                if result:
                    return result
            elif not visited[v] and v == start:
                new_path = path + [(v, u, c)]
                result = find_path(u, end, visited[:], new_path)
                if result:
                    return result

        visited[start] = False
        return None

    def find_all_paths(start, end):
        visited = [False] * (n + 1)
        return find_path(start, end, visited, [])

    min_energy = float('inf')
    for start in range(1, n + 1):
        for end in range(1, n + 1):
            paths = find_all_paths(start, end)
            if paths:
                min_energy = min(min_energy, calculate_energy(paths))

    if min_energy == float('inf'):
        print('Poor girl')
    else:
        print(min_energy)


