def solve():
    n, m, b = map(int, input().split())
    target = []
    for _ in range(b):
        target.append(tuple(map(int, input().split())))

    initial = [[0] * m for _ in range(n)]
    initial[0][0] = 1

    def is_possible(initial, target):
        def get_neighbors(row, col):
            neighbors = []
            if row > 0:
                neighbors.append((row - 1, col))
            if row < n - 1:
                neighbors.append((row + 1, col))
            if col > 0:
                neighbors.append((row, col - 1))
            if col < m - 1:
                neighbors.append((row, col + 1))
            return neighbors

        def find_path(start_row, start_col, end_row, end_col):
            visited = set()
            stack = [(start_row, start_col, [])]

            while stack:
                row, col, path = stack.pop()
                if (row, col) == (end_row, end_col):
                    return path

                if (row, col) in visited:
                    continue

                visited.add((row, col))

                neighbors = get_neighbors(row, col)
                for neighbor_row, neighbor_col in neighbors:
                    if 0 <= neighbor_row < n and 0 <= neighbor_col < m:
                        stack.append((neighbor_row, neighbor_col, path + [(neighbor_row, neighbor_col)]))

            return None

        def check_path(start_row, start_col, end_row, end_col):
            path = find_path(start_row, start_col, end_row, end_col)
            if path:
                return True
            return False

        def check_all_paths():
            for start_row, start_col in [(0, 0)]:
                for end_row, end_col in target:
                    if not check_path(start_row, start_col, end_row, end_col):
                        return False
            return True

        def check_neighbors(row, col):
            neighbors = get_neighbors(row, col)
            for neighbor_row, neighbor_col in neighbors:
                if initial[neighbor_row][neighbor_col] == 1:
                    return True
            return False

        def check_all_neighbors():
            for row in range(n):
                for col in range(m):
                    if initial[row][col] == 1:
                        if check_neighbors(row, col):
                            return False
            return True

        return check_all_paths() and check_all_neighbors()

    if is_possible(initial, target):
        print('possible')
        for r, c in target:
            if r == 1 and c == 1:
                continue
            if r == 1:
                print(f'< {c}')
            elif c == 1:
                print(f'^ {r}')
            elif r == n:
                print(f'> {c}')
            elif c == m:
                print(f'v {r}')
    else:
        print('impossible')


