import math


def dist(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)


def get_grip(img):
    touches = []
    for y in range(len(img)):
        for x in range(len(img[y])):
            if img[y][x] == 'X':
                touches.append((x, y))
    return touches


def get_grip_point(grip):
    x_sum = 0
    y_sum = 0
    for touch in grip:
        x_sum += touch[0]
        y_sum += touch[1]
    return (x_sum / len(grip), y_sum / len(grip))


def get_touch_vectors(grip):
    grip_point = get_grip_point(grip)
    vectors = []
    for touch in grip:
        vectors.append((touch[0] - grip_point[0], touch[1] - grip_point[1]))
    return vectors


def get_grip_spread(grip):
    grip_point = get_grip_point(grip)
    distances = []
    for touch in grip:
        distances.append(dist(grip_point, touch))
    return sum(distances) / len(distances)


def get_touch_rotation(grip):
    vectors = get_touch_vectors(grip)
    rotations = []
    for i in range(len(vectors)):
        x1, y1 = vectors[i]
        x2, y2 = vectors[(i + 1) % len(vectors)]
        rotations.append(math.atan2(y2 - y1, x2 - x1))
    return sum(rotations) / len(rotations)


def classify_gesture(img1, img2):
    grip1 = get_grip(img1)
    grip2 = get_grip(img2)

    if len(grip1) != len(grip2):
        return 'invalid'

    grip_point1 = get_grip_point(grip1)
    grip_point2 = get_grip_point(grip2)

    if dist(grip_point1, grip_point2) == 0:
        return 'invalid'

    touch_vectors1 = get_touch_vectors(grip1)
    touch_vectors2 = get_touch_vectors(grip2)

    touch_rotation1 = get_touch_rotation(grip1)
    touch_rotation2 = get_touch_rotation(grip2)

    grip_spread1 = get_grip_spread(grip1)
    grip_spread2 = get_grip_spread(grip2)

    if abs(grip_spread2 - grip_spread1) < 1e-5:
        return 'invalid'

    if abs(touch_rotation2 - touch_rotation1) < 1e-5:
        return 'invalid'

    if abs(grip_spread2 - grip_spread1) < 1e-5:
        return 'zoom out' if grip_spread2 < grip_spread1 else 'zoom in'

    return 'rotate clockwise' if touch_rotation2 > touch_rotation1 else 'rotate counter-clockwise'


def main():
    img1 = input().split()
    img2 = input().split()

    print(len(grip1), classify_gesture(img1, img2))


