def solve():
    r, c = map(int, input().split())
    start_bed = tuple(map(int, input().split()))
    fridge = tuple(map(int, input().split()))
    p = int(input())
    masters = []
    for _ in range(p):
        masters.append(tuple(map(int, input().split())))

    def is_valid(x, y):
        return 1 <= x <= r and 1 <= y <= c

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny):
                neighbors.append((nx, ny))
        return neighbors

    def find_path(start, end):
        queue = [(start, 0)]
        visited = {start}

        while queue:
            (x, y), turns = queue.pop(0)

            if (x, y) == end:
                return turns

            for nx, ny in get_neighbors(x, y):
                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), turns + 1))

        return float('inf')

    def check_path(path):
        for i in range(len(path) - 1):
            if path[i][0] == path[i+1][0] or path[i][1] == path[i+1][1]:
                return False
        return True

    def find_min_turns():
        min_turns = float('inf')

        for i in range(1 << p):
            master_positions = []
            for j in range(p):
                if (i >> j) & 1:
                    master_positions.append(masters[j])

            for start in get_neighbors(start_bed[0], start_bed[1]):
                for end in get_neighbors(fridge[0], fridge[1]):
                    path = [start]
                    curr = start
                    while curr != end:
                        curr = path[-1]
                        for master in master_positions:
                            if curr == master:
                                break
                        else:
                            next_move = None
                            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                                nx, ny = curr[0] + dx, curr[1] + dy
                                if is_valid(nx, ny) and (nx, ny) not in path:
                                    next_move = (nx, ny)
                                    break
                            if next_move is None:
                                break
                            path.append(next_move)
                        else:
                            break
                    else:
                        path.append(end)
                        if check_path(path):
                            min_turns = min(min_turns, len(path))

        return min_turns if min_turns != float('inf') else 'IMPOSSIBLE'

    print(find_min_turns())


