        from collections import deque
        queue = deque([(start, 0)])
        visited = {start}

        while queue:
            current_word, steps = queue.popleft()

            if current_word == end:
                return steps

            for i in range(len(current_word)):
                for j in range(26):
                    new_word = list(current_word)
                    new_word[i] = chr(ord('A') + j)
                    new_word = ''.join(new_word)

                    if new_word in dictionary and new_word not in visited:
                        visited.add(new_word)
                        queue.append((new_word, steps + 1))

        return float('inf')

    def find_best_word(dictionary, start_word, end_word):
        best_word = ''
        min_steps = float('inf')

        for word in dictionary:
            ladder_length = calculate_ladder_length(start_word, word, dictionary) + \
                calculate_ladder_length(word, end_word, dictionary)

            if ladder_length < min_steps:
                min_steps = ladder_length
                best_word = word

        if min_steps == float('inf'):
            return '', -1
        else:
            return best_word, min_steps

