def solve():
    n = int(input())
    edges = []
    for _ in range(2 * (n - 1)):
        edges.append(list(map(int, input().split())))

    def check_solution(solution):
        left_edges = []
        right_edges = []
        for i in range(2 * (n - 1)):
            if solution[i] == 'L':
                left_edges.append(edges[i])
            else:
                right_edges.append(edges[i])

        def check_tree(edges, root):
            adj = [[] for _ in range(n + 1)]
            for u, v in edges:
                adj[u].append(v)
                adj[v].append(u)

            visited = [False] * (n + 1)
            stack = [root]
            visited[root] = True

            while stack:
                node = stack.pop()
                for neighbor in adj[node]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        stack.append(neighbor)

            for i in range(1, n + 1):
                if not visited[i]:
                    return False
            return True

        if not check_tree(left_edges, 1):
            return False
        if not check_tree(right_edges, n):
            return False
        return True

    def generate_all_solutions(n):
        import itertools

        def generate_subsets(index, current_solution):
            if index == 2 * (n - 1):
                if check_solution(current_solution):
                    yield ''.join(current_solution)
                return

            for option in ['L', 'R']:
                current_solution.append(option)
                yield from generate_subsets(index + 1, current_solution)
                current_solution.pop()

        for solution in generate_subsets(0, []):
            yield solution

    possible_solutions = list(generate_all_solutions(n))

    if not possible_solutions:
        print('impossible')
    else:
        print(possible_solutions[0])


