    h, w = map(int, input().split())
    grid = []
    for _ in range(h):
        grid.append(input())

    s_row = -1
    s_col = -1
    t_row = -1
    t_col = -1

    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'S':
                s_row = i
                s_col = j
            elif grid[i][j] == 'T':
                t_row = i
                t_col = j

    if s_row == t_row or s_col == t_col:
        print(0)
        return

    def is_valid(r, c):
        return 0 <= r < h and 0 <= c < w

    def get_neighbors(r, c):
        neighbors = []
        for dr in [-1, 0, 1]:
            for dc in [-1, 0, 1]:
                nr = r + dr
                nc = c + dc
                if is_valid(nr, nc):
                    neighbors.append((nr, nc))
        return neighbors

    def bfs(start_row, start_col):
        queue = [(start_row, start_col)]
        visited = set()
        visited.add((start_row, start_col))

        while queue:
            r, c = queue.pop(0)
            if r == t_row and c == t_col:
                return True

            for nr, nc in get_neighbors(r, c):
                if (nr, nc) not in visited:
                    visited.add((nr, nc))
                    queue.append((nr, nc))
        return False

    def find_min_leaves_to_remove():
        for i in range(1 << (h * w)):
            leaves_to_remove = []
            for j in range(h * w):
                if (i >> j) & 1:
                    leaves_to_remove.append(j)

            if len(leaves_to_remove) == 0:
                continue

            grid_copy = [row[:] for row in grid]

            for leaf_index in leaves_to_remove:
                r = leaf_index // w
                c = leaf_index % w
                grid_copy[r][c] = '.'

            if bfs(s_row, s_col):
                continue

            if not bfs(s_row, s_col):
                return len(leaves_to_remove)

        return -1

    print(find_min_leaves_to_remove())


