def solve():
    s = input()
    n = len(s)

    def factorize(s):
        if not s:
            return []

        for i in range(1, n + 1):
            if s[:i] * (n // i) == s:
                return [(s[:i], i)]
            if s[:i] * (n // i) == s[:i] * (n // i) * (n // (i * i)):
                return [(s[:i], i), (s[:i], n // (i * i))]
        return []

    def get_weight(factoring):
        weight = 0
        for factor in factoring:
            weight += len(factor[0]) * factor[1]
        return weight

    def find_maximal_factorization(s):
        factorings = factorize(s)
        if not factorings:
            return [(s, 1)]

        max_weight = float('inf')
        best_factoring = None

        for factoring in factorings:
            weight = get_weight(factoring)
            if weight < max_weight:
                max_weight = weight
                best_factoring = factoring

        return best_factoring

    factoring = find_maximal_factorization(s)
    print(get_weight(factoring))


