    packages = []
    for _ in range(n):
        packages.append(tuple(map(int, input().split())))

    def calculate_moves(path):
        x, y = 0, 0
        moves = 0
        for move in path:
            if move == 'R':
                x += 1
            else:
                y += 1
            moves += 1
        return moves

    def find_lexicographically_smallest_path(packages):
        import itertools

        min_moves = float('inf')
        best_path = None

        for path in itertools.permutations(['R'] * (packages[0][0] - 1) + ['U'] * (packages[0][1] - 1)):
            current_path = ''.join(path)
            moves = calculate_moves(current_path)
            if moves < min_moves:
                min_moves = moves
                best_path = current_path

        return best_path

    def check_if_possible(packages):
        import itertools

        for path in itertools.permutations(['R'] * (packages[0][0] - 1) + ['U'] * (packages[0][1] - 1)):
            current_path = ''.join(path)
            moves = calculate_moves(current_path)
            if moves == n:
                return True
        return False

    if not check_if_possible(packages):
        print('NO')
        return

    print('YES')
    path = find_lexicographically_smallest_path(packages)
    print(path)


