    def find_cycle(arr, p):
        visited = [False] * len(arr)
        stack = [0]
        visited[0] = True
        cycle = []
        while stack:
            node = stack.pop()
            cycle.append(node)
            for i in range(len(p)):
                if arr[p[i]-1] == arr[node] and not visited[p[i]-1]:
                    stack.append(p[i]-1)
                    visited[p[i]-1] = True
                    break
        return cycle

    def has_cycle(arr, p):
        cycle = find_cycle(arr, p)
        if len(cycle) == len(arr):
            return True
        else:
