    umbrellas = []
    for _ in range(m):
        umbrellas.append(list(map(int, input().split())))

    def check_path(start, end):
        visited = [False] * (a + 1)
        visited[0] = True

        queue = [(start, 0)]  # (current_position, total_fatigue)

        while queue:
            curr_pos, curr_fatigue = queue.pop(0)

            if curr_pos == end:
                return curr_fatigue

            for i in range(n):
                l, r = rain_segments[i]
                if l <= curr_pos and curr_pos + 1 <= r:
                    for j in range(m):
                        umbrella_pos, umbrella_weight = umbrellas[j]
                        if umbrella_pos == curr_pos:
                            new_fatigue = curr_fatigue + umbrella_weight
                            if not visited[curr_pos + 1]:
                                visited[curr_pos + 1] = True
                                queue.append((curr_pos + 1, new_fatigue))
                            break
        return -1

    result = check_path(0, a)
    if result == -1:
        print('-1')
    else:
        print(result)


