def solve():
    n = int(input())
    dp = {}

    def count_ways(length):
        if length == 0:
            return 1
        if length < 0:
            return 0
        if length in dp:
            return dp[length]
        ways = 0
        ways += count_ways(length - 1)
        ways += count_ways(length - 2)
        dp[length] = ways
        return ways
    print(count_ways(n))


