def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n ** 0.5) + 1):
        if n % i == 0:
            return False
    return True


def solve():
    n, m = map(int, input().split())
    matrix = []
    for _ in range(n):
        matrix.append(list(map(int, input().split())))

    def check_prime_row(row):
        for val in row:
            if not is_prime(val):
                return False
        return True

    def check_prime_col(col):
        for i in range(n):
            if not is_prime(matrix[i][col]):
                return False
        return True

    def get_moves_to_prime_row(row):
        moves = 0
        for i in range(len(row)):
            if not is_prime(row[i]):
                moves += (1 + (row[i] - 1) % (i + 1))
        return moves

    def get_moves_to_prime_col(col):
        moves = 0
        for i in range(len(col)):
            if not is_prime(matrix[i][col]):
                moves += (1 + (matrix[i][col] - 1) % (col + 1))
        return moves

    moves = float('inf')

    # Check rows
    for i in range(n):
        if check_prime_row(matrix[i]):
            moves = 0
            break
        else:
            moves = min(moves, get_moves_to_prime_row(matrix[i]))

    # Check columns
    for j in range(m):
        col = [matrix[i][j] for i in range(n)]
        if check_prime_col(col):
            moves = 0
            break
        else:
            moves = min(moves, get_moves_to_prime_col(j))

    print(moves)


