def solve():
    n, m = map(int, input().split())
    a = []
    for _ in range(n):
        a.append(list(map(int, input().split())))

    def can_transform(b, a):
        for i in range(n):
            for j in range(m):
                if b[i][j] != a[i][j]:
                    return False
        return True

    def find_min_rows(a):
        min_rows = float('inf')
        for rows in range(1, n + 1):
            for i in range(rows):
                b = [a[i]]
                for j in range(i + 1, rows):
                    b.append(a[j])
                for k in range(rows, n):
                    b.append(b[n - k - 1])
                if can_transform(b, a):
                    min_rows = min(min_rows, rows)
        return min_rows
    print(find_min_rows(a))


