def solve():
    n, k = map(int, input().split())
    mod = 10 ** 6 + 3

    def binom(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        res = 1
        if k > n // 2:
            k = n - k
        for i in range(k):
            res = res * (n - i) % mod
            res = res * pow(i + 1, mod - 2, mod) % mod
        return res
    total_ways = pow(2, n, mod) ** k
    favorable_ways = 0
    for i in range(2, k + 1):
        favorable_ways = (favorable_ways + binom(k, i) * pow(2, n, mod) ** (
            k - i)) % mod
    print(favorable_ways, total_ways)


