    for u, v in edges:
        children[u].append(v)
        children[v].append(u)

    def count_leaves(node, parent):
        count = 0
        for child in children[node]:
            if child != parent:
                count += count_leaves(child, node)
        if count == 0:
            return 1
        else:
            return count
    leaves = []
    for i in range(n):
        if len(children[i]) == 1 and i != 0:
            leaves.append(i)
    if len(leaves) == 0:
