def solve():
    int(input())
    a = list(map(int, input().split()))

    def calculate(a):
        n = len(a)
        dp = [[0] * n for _ in range(n)]
        for length in range(1, n + 1):
            for i in range(n - length + 1):
                j = i + length - 1
                if length == 1:
                    dp[i][j] = a[i]
                elif length == 2:
                    dp[i][j] = a[i] + a[j]
                else:
                    dp[i][j] = max(a[i] + min(dp[i + 1][j], dp[i + 2][j]),
                        a[j] + min(dp[i][j - 1], dp[i + 1][j - 1]))
        return dp[0][n - 1]

    def find_solution(a):
        n = len(a)
        dp = [[(0) for _ in range(n + 1)] for _ in range(n + 1)]
        for length in range(1, n + 1):
            for i in range(n - length + 1):
                j = i + length - 1
                if length == 1:
                    dp[i][j] = a[i]
                elif length == 2:
                    dp[i][j] = a[i] + a[j]
                else:
                    dp[i][j] = max(a[i] + min(dp[i + 1][j], dp[i + 2][j]),
                        a[j] + min(dp[i][j - 1], dp[i + 1][j - 1]))
        ans = float('-inf')
        best_cut = []
        for i in range(n):
            for j in range(i + 1, n):
                temp_a = a[:i] + a[j + 1:]
                temp_sum = calculate(temp_a)
                if temp_sum > ans:
                    ans = temp_sum
                    best_cut = [i + 1, j + 1]
        return ans, best_cut

    ans, cut = find_solution(a)
    print(ans, len(cut))
    print(*cut)


