    def is_palindromic(path):
        return path == path[::-1]

    def generate_paths(current_row, current_col):
        if current_row == n:
            if current_col == m:
                return [tuple(path)]
            else:
                return []
        paths = []
        paths.extend(generate_paths(current_row + 1, current_col))
        paths.extend(generate_paths(current_row, current_col + 1))
        return paths

    def check_all_paths(matrix):
        min_changes = float('inf')
        for i in range(n):
            for j in range(m):
                for k in range(i, n):
                    for l in range(j, m):
                        path = []
                        current_row = 0
                        current_col = 0
                        while current_row != i or current_col != j:
                            path.append((current_row, current_col))
                            if current_row == i:
                                current_col = current_col + 1
                            elif current_col == j:
                                current_row = current_row + 1
                            else:
                                if matrix[current_row][current_col - 1
                                    ] == 1:
                                    current_row = current_row + 1
                                else:
                                    current_col = current_col + 1
                        path.append((i, j))
                        if current_row == n - 1 and current_col == m - 1:
                            path.append((n - 1, m - 1))
                        if is_palindromic(path):
                            min_changes = min(min_changes, len(path))
        return min_changes
    min_changes = float('inf')
