        r, c = pawn
        return 0 <= r - 1 < 8

    def can_move_down(pawn):
        r, c = pawn
        return 0 <= r + 1 < 8

    def can_move(pawn, board):
        r, c = pawn
        if board[r][c] == 'B':
            return can_move_up(pawn)
        else:
            return can_move_down(pawn)

    def get_possible_moves(pawns, board):
        moves = []
        for pawn in pawns:
            if board[pawn[0]][pawn[1]] == 'W':
                if can_move(pawn, board):
                    moves.append(pawn)
        return moves

    def get_pawns(board):
        pawns = []
        for r in range(8):
            for c in range(8):
                if board[r][c] == 'W':
                    pawns.append((r, c))
                elif board[r][c] == 'B':
                    pawns.append((r, c))
        return pawns

    def simulate_game(pawns_a, pawns_b):
        board = [list(row) for row in [''.join(row) for row in board]]
