                    temp_board[move[0]][move[1]] = '.'
                    temp_board[move[0] + 1][move[1]] = 'B'
                    if (move[0] + 1, move[1]) in pawns_a:
                        return False
                    if not get_possible_moves(pawns_a, temp_board):
                        return False
            turn += 1
