        rotated = [''] * h
        for i in range(h):
            for j in range(w):
                rotated[i] += image[w - 1 - j][i]
        return rotated

    def flip_horizontally(image):
        flipped = []
        for row in image:
            flipped.append(row[::-1])
        return flipped

    def zoom_in(image):
        zoomed = []
        for i in range(0, len(image), 2):
            row = ''
            for j in range(0, len(image[i]), 2):
                row += image[i][j]
            zoomed.append(row)
        return zoomed

    rotated_image = rotate_clockwise(image)
    flipped_image = flip_horizontally(rotated_image)
    final_image = zoom_in(flipped_image)

    for row in final_image:
        print(row + row[::-1])


