def solve():
    n = int(input())
    edges = []
    for _ in range(n):
        l, r = map(int, input().split())
        edges.append((l, r))

    adj = [[] for _ in range(n)]
    for i in range(n):
        for j in range(i + 1, n):
            l1, r1 = edges[i]
            l2, r2 = edges[j]
            if l1 < r2 and l2 < r1:
                adj[i].append(j)
                adj[j].append(i)

    def is_tree(edges, n):
        adj = [[] for _ in range(n)]
        for i, j in edges:
            adj[i].append(j)
            adj[j].append(i)

        visited = [False] * n
        parent = [-1] * n

        def dfs(node, par):
            visited[node] = True
            parent[node] = par
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    if dfs(neighbor, node):
                        return True
                elif neighbor != par:
                    return True
            return False

        if dfs(0, -1):
            return False

        for i in range(n):
            if not visited[i]:
                return False

        return True

    if is_tree(edges, n):
        print('YES')
    else:
        print('NO')


