def solve():
    n, k = map(int, input().split())
    w = list(map(int, input().split()))

    MOD = 10 ** 9 + 7

    def combinations(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = res * (n - i) % MOD
            res = res * pow(i + 1, MOD - 2, MOD) % MOD
        return res

    def calculate_partitions(n, k, w):
        if k == 0:
            return 0
        if k == 1:
            return pow(2, n, MOD)

        dp = {}

        def calculate_partitions_recursive(index, partitions):
            if (index, partitions) in dp:
                return dp[(index, partitions)]

            if partitions == 0:
                return 1

            if index == n:
                if partitions == 0:
                    return 1
                else:
                    return 0

            total_partitions = 0

            for i in range(index, n):
                new_partitions = partitions - 1
                total_partitions = (total_partitions + 
                    calculate_partitions_recursive(i + 1, new_partitions) * 
                    pow(2, n - i - 1, MOD)) % MOD

            dp[(index, partitions)] = total_partitions
            return total_partitions

        return calculate_partitions_recursive(0, k)

    def calculate_weight(partition):
        total_weight = 0
        for subset in partition:
            subset_weight = 0
            for element in subset:
                subset_weight = (subset_weight + w[element - 1]) % MOD
            total_weight = (total_weight + subset_weight) % MOD
        return total_weight

    def generate_partitions(index, current_partition, partitions_count):
        if len(current_partition) == partitions_count:
            partitions.append(current_partition[:])
            return

        if index == n:
            return

        for i in range(index, n):
            current_partition.append(i + 1)
            generate_partitions(i + 1, current_partition, partitions_count)
            current_partition.pop()

    partitions = []
    generate_partitions(0, [], k)

    total_weight = 0
    for partition in partitions:
        total_weight = (total_weight + calculate_weight(partition)) % MOD

    print(total_weight)


solve()

