    MOD = 998244353

    def power(x, y, m):
        res = 1
        x = x % m
        while y > 0:
            if y % 2 == 1:
                res = res * x % m
            x = x * x % m
            y //= 2
        return res

    def mod_inverse(x, m):
        return power(x, m - 2, m)

    def ncr(n, r, m):
        fact = [1] * (n + 1)
        inv_fact = [1] * (n + 1)
        for i in range(1, n + 1):
            fact[i] = i * fact[i - 1] % m
        inv_fact[n] = mod_inverse(fact[n], m)
        for i in range(n - 1, 0, -1):
            inv_fact[i] = inv_fact[i + 1] * (i + 1) % m
        return fact[n] * inv_fact[r] % m * inv_fact[n - r] % m

    def expected_value(n, a, m):
        dp = [0] * (n + 1)
        dp[0] = a[0]
        for i in range(1, n):
            dp[i] = (dp[i - 1] + a[i]) % m
        return dp[n - 1] * ncr(n - 1, n - 2, m) % m

    print(expected_value(len(a), a, MOD) * pow(2, len(a) - 1, MOD) % MOD)


