def solve():
    n = int(input())
    points = []
    for _ in range(n):
        points.append(list(map(int, input().split())))

    def check(m, d, w):
        x = 0
        y = 0
        for i in range(m):
            if w[i] == 'L':
                x -= d[i]
            elif w[i] == 'R':
                x += d[i]
            elif w[i] == 'D':
                y -= d[i]
            elif w[i] == 'U':
                y += d[i]
        for i in range(n):
            if x != points[i][0] or y != points[i][1]:
                return False
        return True

    def find_arm():
        for m in range(1, 41):
            for d in product(range(1, 11), repeat=m):
                d = list(d)
                for w in product('LRUD', repeat=m):
                    if check(m, d, w):
                        return m, d, w
        return -1, None, None

    def generate_output(m, d, w):
        print(m)
        print(*d)
        print(*w)

    def check_solution():
        for m in range(1, 41):
            for d in product(range(1, 11), repeat=m):
                d = list(d)
                for w in product('LRUD', repeat=m):
                    if check(m, d, w):
                        return True
        return False

    def solve_with_backtracking(index, current_points, current_m, current_d, current_w):
        nonlocal found_solution
        if index == n:
            if check_solution():
                found_solution = True
                generate_output(current_m, current_d, current_w)
            return
        for m_val in range(current_m + 1, 41):
            for d_val in product(range(1, 11), repeat=m_val):
                d_val = list(d_val)
                for w_val in product('LRUD', repeat=m_val):
                    if check(m_val, d_val, w_val):
                        solve_with_backtracking(index + 1, current_points + [(current_points[-1][0] + d_val[-1], current_points[-1][1] + d_val[-2])], m_val, d_val, current_w + list(w_val))
    found_solution = False
    solve_with_backtracking(0, [(0, 0)], 0, [], '')
    if not found_solution:
        m, d, w = find_arm()
        if m == -1:
            print(-1)
        else:
            generate_output(m, d, w)


