def solve():
    n = int(input())
    songs = []
    for _ in range(n):
        artist, t, *indices = input().split()
        songs.append((artist, int(t), list(map(int, indices))))

    def is_valid(playlist):
        if len(playlist) != 9:
            return False
        if len(set(playlist)) != 9:
            return False
        for i in range(len(playlist) - 1):
            last_song_index = playlist[i] - 1
            next_possible_songs = songs[last_song_index][1:]
            if songs[last_song_index][0] not in next_possible_songs:
                return False
        return True

    def find_playlist(current_playlist, remaining_songs):
        if len(current_playlist) == 9:
            if is_valid(current_playlist):
                return current_playlist
        for song_index in remaining_songs:
            new_playlist = current_playlist + [song_index]
            new_remaining_songs = remaining_songs - {song_index}
            result = find_playlist(new_playlist, new_remaining_songs)
            if result is not None:
                return result
        return None

    remaining_songs = set(range(1, n + 1))
    playlist = find_playlist([], remaining_songs)
    if playlist is None:
        print('fail')
    else:
        print(*playlist)


