def solve():
    n = int(input())
    frogs = []
    for _ in range(n):
        x, d = map(int, input().split())
        frogs.append((x, d))

    best_position = -1
    best_height = -1

    for i in range(1 << n):
        tower_height = 0
        positions = []

        for j in range(n):
            if (i >> j) & 1:
                positions.append(frogs[j][0] + tower_height * frogs[j][1])

        if not positions:
            continue

        positions.sort()

        for k in range(len(positions) - 1):
            tower_height = max(tower_height, positions[k+1] - positions[k])

        if not best_position == -1:
            best_position = min(best_position, positions[-1])
            best_height = max(best_height, tower_height)
        else:
            best_position = positions[-1]
            best_height = tower_height

        for k in range(len(positions)):
            if positions[k] > best_position:
                break
            for l in range(k + 1, len(positions)):
                if positions[l] > best_position:
                    break
                if positions[k] + positions[l] > best_position:
                    break
                if positions[k] + positions[l] + tower_height > best_height:
                    best_position = positions[k] + positions[l]
                    best_height = positions[k] + positions[l] + tower_height

    print(best_position, best_height)


