            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = res * (n - i) % MOD
            res = res * pow(i + 1, MOD - 2, MOD) % MOD
        return res

    def count_arrangements(arrangement, counts, banned, seq):
        n = len(arrangement)
        dp = {}

        def solve(index, color_count):
            if index == n:
                return 1
            if (index, tuple(sorted(color_count.items()))) in dp:
                return dp[index, tuple(sorted(color_count.items()))]
            ways = 0
            for i in range(1, len(seq) + 1):
                if i > n - index:
                    break
                found = False
                for j in range(index, index + i):
                    if arrangement[j] == seq[j - index]:
                        found = True
                        break
                if found:
                    continue
                for color, count in list(color_count.items()):
                    if color in banned:
                        continue
                    new_count = dict(color_count)
                    new_count[color] -= 1
                    if new_count[color] >= 0:
                        ways = (ways + solve(index + i, new_count)) % MOD
            dp[index, tuple(sorted(color_count.items()))] = ways
            return ways
        return solve(0, dict(zip(range(1, len(counts) + 1), counts)))
    total_arrangements = 0
    for i in range(1 << len(seq)):
        temp_counts = counts[:]
        arrangement = []
        color_count = dict(zip(range(1, len(counts) + 1), temp_counts))
        for j in range(len(seq)):
            if i >> j & 1:
                arrangement.append(seq[j])
                color_count[seq[j]] -= 1
        total_arrangements = (total_arrangements + combinations(
