    def min_operations(n):
        count = 0
        temp = n
        while temp > 0:
            count += temp % 2
            temp //= 2
        return count

    def count_special_numbers(n_binary, k):
        count = 0
        for i in range(1 << len(n_binary)):
            num = int(n_binary, 2) & i
            if min_operations(num) == k:
