def solve():
    n = int(input())
    dictionary = []
    for _ in range(n):
        dictionary.append(input())

    start = input()
    end = input()

    import heapq

    def get_neighbors(word, dictionary):
        neighbors = []
        for i in range(len(word)):
            for char_code in range(ord('A'), ord('Z') + 1):
                new_word = list(word)
                new_word[i] = chr(char_code)
                new_word = ''.join(new_word)
                if new_word in dictionary:
                    neighbors.append(new_word)
        return neighbors

    start_neighbors = get_neighbors(start, dictionary)
    end_neighbors = get_neighbors(end, dictionary)

    start_distances = {word: float('inf') for word in dictionary}
    end_distances = {word: float('inf') for word in dictionary}

    start_distances[start] = 0
    end_distances[end] = 0

    start_heap = [(0, start)]
    end_heap = [(0, end)]

    while start_heap or end_heap:
        dist_start, curr_start = heapq.heappop(start_heap)
        dist_end, curr_end = heapq.heappop(end_heap)

        if dist_start > start_distances[curr_start] or dist_end > end_distances[curr_end]:
            continue

        if curr_start == curr_end:
            break

        if dist_start < dist_end:
            for neighbor in start_neighbors:
                if start_distances[neighbor] == float('inf'):
                    start_distances[neighbor] = dist_start + 1
                    heapq.heappush(start_heap, (dist_start + 1, neighbor))
        else:
            for neighbor in end_neighbors:
                if end_distances[neighbor] == float('inf'):
                    end_distances[neighbor] = dist_end + 1
                    heapq.heappush(end_heap, (dist_end + 1, neighbor))

    shortest_path = float('inf')
    best_word = ''

    for word in dictionary:
        if start_distances[word] != float('inf') and end_distances[word] != float('inf'):
            shortest_path = min(shortest_path, start_distances[word] + end_distances[word])

    if shortest_path == float('inf'):
        print('0', -1)
    else:
        for word in dictionary:
            if start_distances[word] != float('inf') and end_distances[word] != float('inf') and start_distances[word] + end_distances[word] == shortest_path:
                if word < best_word or best_word == '':
                    best_word = word

        print(best_word, shortest_path)


