def solve():
    h, w = map(int, input().split())
    grid = []
    for _ in range(h):
        grid.append(input())

    def find_frog(grid):
        for i in range(h):
            for j in range(w):
                if grid[i][j] == 'S':
                    return i, j
        return -1, -1

    def is_reachable(start_row, start_col, end_row, end_col, grid):
        visited = [[False for _ in range(w)] for _ in range(h)]
        queue = [(start_row, start_col)]
        visited[start_row][start_col] = True
        while queue:
            row, col = queue.pop(0)
            if row == end_row and col == end_col:
                return True
            for dr in [-1, 0, 1]:
                for dc in [-1, 0, 1]:
                    new_row = row + dr
                    new_col = col + dc
                    if 0 <= new_row < h and 0 <= new_col < w:
                        if not visited[new_row][new_col]:
                            if grid[new_row][new_col] != '.':
                                queue.append((new_row, new_col))
                                visited[new_row][new_col] = True
        return False

    def get_reachable_leaves(start_row, start_col, grid):
        reachable_leaves = []
        visited = [[False for _ in range(w)] for _ in range(h)]
        queue = [(start_row, start_col)]
        visited[start_row][start_col] = True
        while queue:
            row, col = queue.pop(0)
            reachable_leaves.append((row, col))
            for dr in [-1, 0, 1]:
                for dc in [-1, 0, 1]:
                    new_row = row + dr
                    new_col = col + dc
                    if 0 <= new_row < h and 0 <= new_col < w:
                        if not visited[new_row][new_col]:
                            if grid[new_row][new_col] != '.':
                                queue.append((new_row, new_col))
                                visited[new_row][new_col] = True
        return reachable_leaves

    def remove_leaves_and_check(grid, leaves_to_remove):
        new_grid = [row[:] for row in grid]
        for r, c in leaves_to_remove:
            new_grid[r][c] = '.'
        reachable_t = get_reachable_leaves(h - 1, w - 1, new_grid)
        if len(reachable_t) == 0:
            return False
        return True

    frog_row, frog_col = find_frog(grid)
    leaves_to_remove = []
    for i in range(h):
        for j in range(w):
            if grid[i][j] != 'S' and grid[i][j] != 'T':
                reachable = get_reachable_leaves(frog_row, frog_col, grid)
                if (i, j) not in reachable:
                    leaves_to_remove.append((i, j))
    if len(leaves_to_remove) == 0:
        print(-1)
        return
    min_leaves_to_remove = len(leaves_to_remove)
    for i in range(1 << len(leaves_to_remove)):
        curr_leaves_to_remove = []
        for j in range(len(leaves_to_remove)):
            if (i >> j) & 1:
                curr_leaves_to_remove.append(leaves_to_remove[j])
        if len(curr_leaves_to_remove) > 0:
            if remove_leaves_and_check(grid, curr_leaves_to_remove):
                min_leaves_to_remove = min(min_leaves_to_remove, len(
                    curr_leaves_to_remove))
    print(min_leaves_to_remove)


solve()

