    def factorize(s):
        i = 0
        while i < n:
            for j in range(i + 1, n + 1):
                sub = s[i:j]
                if sub * (n // j) == s:
                    yield sub, j
            i += 1
    factors = []
    i = 0
    while i < n:
        for j in range(i + 1, n + 1):
            sub = s[i:j]
            if sub * (n // j) == s:
                factors.append((sub, j))
        i += 1
    weights = []
    for sub, count in factors:
        weight = len(sub) * count
        for k in range(1, count):
            if sub * k == s:
                weight -= k
                break
        weights.append(weight)
    if not weights:
        print(len(s))
        return
    min_weight = min(weights)
    print(min_weight)


