            elif time < green + yellow + red:
                return 1
            else:
                return 0
        else:
            if time < green:
                return 0
            elif time < green + yellow:
                return 0
            elif time < green + yellow + red:
                return 1
            else:
                return 0

    def find_closest(time):
        closest_time = float('inf')
        closest_color = -1
        for time_val, color in observations:
            if time_val <= time:
                if color == 0:
                    if time_val < closest_time:
                        closest_time = time_val
                        closest_color = 0
                elif color == 1:
                    if time_val < closest_time:
                        closest_time = time_val
                        closest_color = 1
                elif time_val < closest_time:
                    closest_time = time_val
                    closest_color = 2
        return closest_time, closest_color

    def binary_search(low, high):
        if low > high:
            return 0
        mid = (low + high) // 2
        closest_time, closest_color = find_closest(mid)
        if closest_time == float('inf'):
            return 0
        if closest_color == -1:
            return 0
        if closest_color == c_q:
            return 1
        elif closest_color < c_q:
            return binary_search(mid + 1, high)
        else:
            return binary_search(low, mid - 1)
    print(binary_search(0, t_q))


