def solve():
    line = input().split()
    input_molecule = line[0]
    k = int(line[1])
    output_molecule = input().strip()

    def parse_molecule(molecule):
        parts = []
        i = 0
        while i < len(molecule):
            if not molecule[i].isalpha():
                if not molecule[i].isdigit():
                    parts.append((molecule[i], 1))
                    i += 1
                else:
                    num_str = ''
                    while i < len(molecule) and molecule[i].isdigit():
                        num_str += molecule[i]
                        i += 1
                    parts.append((molecule[i - 1], int(num_str)))
            else:
                parts.append((molecule[i], 1))
                i += 1
        return parts

    input_parts = parse_molecule(input_molecule)
    output_parts = parse_molecule(output_molecule)

    def can_construct(input_parts, output_parts):
        input_copy = input_parts[:]
        for atom, count in output_parts:
            found = False
            for i in range(len(input_copy)):
                if input_copy[i][0] == atom:
                    if input_copy[i][1] >= count:
                        input_copy[i] = (atom, input_copy[i][1] - count)
                        found = True
                        break
            if not found:
                return False
        return True
    max_output = 0
    for i in range(k + 1):
        temp_input = input_molecule * i
        input_parts = parse_molecule(temp_input)
        if can_construct(input_parts, output_parts):
            max_output = max(max_output, i)
    print(max_output)


