def solve():
    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(input())

    def is_valid(r, c):
        return 0 <= r < n and 0 <= c < m

    def is_adjacent(r1, c1, r2, c2):
        return abs(r1 - r2) <= 1 and abs(c1 - c2) <= 1

    def is_safe(r, c):
        return is_valid(r, c) and grid[r][c] == '.'

    def place_coffee_shops():
        for r in range(n):
            for c in range(m):
                if grid[r][c] == '.':
                    if not (is_adjacent(r, c, r - 1, c) or is_adjacent(r,
                        c, r + 1, c) or is_adjacent(r, c, r, c - 1) or is_adjacent(
                        r, c, r, c + 1)):
                        grid[r] = grid[r][:c] + 'E' + grid[r][c + 1:]
                        return True
        return False

    def check_constraints():
        for r in range(n):
            for c in range(m):
                if grid[r][c] == 'E':
                    for dr, dc in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                        nr, nc = r + dr, c + dc
                        if is_safe(nr, nc):
                            if grid[nr][nc] == 'E':
                                return False
        return True

    def print_grid():
        for row in grid:
            print(row)

    def backtrack(r, c):
        if r == n:
            if check_constraints():
                return True
            return False

        if c == m:
            return backtrack(r + 1, 0)

        if grid[r][c] == '.':
            if place_coffee_shops():
                return True

        if c + 1 < m:
            if backtrack(r, c + 1):
                return True

        return False

    backtrack(0, 0)
    print_grid()


solve()

