    n, m, k = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(list(map(int, input().split())))
    MOD = 10**9 + 7

    def calculate_xor_sum(path):
        xor_sum = 0
        for i in range(len(path)):
            for j in range(len(path[i])):
                xor_sum ^= path[i][j]
        return xor_sum

    def count_paths(current_row, current_col, current_path, visited):
        if current_row == n - 1 and current_col == m - 1:
            if calculate_xor_sum(current_path) == k:
                return 1
            else:
                return 0
        paths = 0
        if current_row + 1 < n:
            if (current_row + 1, current_col) not in visited:
                new_path = current_path + [grid[current_row + 1][current_col]]
                visited.add((current_row + 1, current_col))
                paths = (paths + count_paths(current_row + 1, current_col,
                    new_path, visited)) % MOD
                visited.remove((current_row + 1, current_col))
        if current_col + 1 < m:
            if (current_row, current_col + 1) not in visited:
                new_path = current_path + [grid[current_row][current_col + 1]]
                visited.add((current_row, current_col + 1))
                paths = (paths + count_paths(current_row, current_col + 1,
                    new_path, visited)) % MOD
                visited.remove((current_row, current_col + 1))
        return paths
    visited = set()
    visited.add((0, 0))
    print(count_paths(0, 0, [grid[0][0]], visited))


